internal static class Day6
{
    public static void Part1(bool part2)
    {
        var continuousDifferentCharacters = part2 ? 14 : 4;
        var input = "";
        var previousChar = new Queue<char>();
        for (var i = 0; i < continuousDifferentCharacters; i++)
        {
            previousChar.Enqueue('$');
        }
        var res = 0;

        for (var i = 0; i < input.Length; i++)
        {
            previousChar.Dequeue();
            previousChar.Enqueue(input[i]);

            if (previousChar.GroupBy(x => x).Count() == continuousDifferentCharacters)
            {
                res = i + 1;
                break;
            }
        }

        Console.WriteLine(res);
    }
}